
import java.util.Scanner;

public class Questions {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		Scanner sc = new Scanner(System.in);
		System.out.print("Enter a digit : ");
		int n = sc.nextInt();
		while(n>0) {
			if(n%2==0) {
			System.out.print(n);
			n--;
		}

	}

}
}
